/* 
Destroys the saved token in `$HOME/.requiresaferc` if it exists.
*/
var updateConfig = require('../update-config');


module.exports = function (config, callback) {
    updateConfig({token: '', username: ''}, function (err) {
        if (!err) {
            callback(null, 'You\'ve been logged out');
        } else {
            callback(err);
        }
    });
};
